import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';

const CreateAccount = () => {
  const [formData, setFormData] = useState({ name: '', email: '', password: '' });
  const [showSuccessModal, setShowSuccessModal] = useState(false);
  const [showAddAnother, setShowAddAnother] = useState(false);
  const [userEmail, setUserEmail] = useState('');

  const handleInputChange = (e) => {
    const { id, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [id]: value }));
  };

  const validateForm = () => {
    const { name, email, password } = formData;
    if (name.trim() === '') {
      alert('Name field is required.');
      return false;
    }
    if (email.trim() === '') {
      alert('Email field is required.');
      return false;
    }
    if (password.length < 8) {
      alert('Password must be at least 8 characters long.');
      return false;
    }
    return true;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      const { name, email, password } = formData;
      axios.post('http://backend-dev2.us-east-1.elasticbeanstalk.com/store_login', { name, email, password })
        .then(() => {
          setShowSuccessModal(true);
          setShowAddAnother(true);
          setFormData({ name: '', email: '', password: '' });
          setUserEmail(email);
        })
        .catch((error) => {
          console.error(error);
          alert('Error creating account.');
        });
    }
  };

  const handleAddAnother = () => {
    setShowAddAnother(false);
  };

  const handleLogout = () => {
    localStorage.removeItem('userEmail');
    setUserEmail('');
    alert('You have logged out.');
  };

  return (
    <div>
      <nav className="navbar navbar-expand-lg navbar-dark" style={{ backgroundColor: '#004571' }}>
        <a className="navbar-brand" href="/">Very Bad Bank</a>
        <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNavAltMarkup" aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNavAltMarkup">
          <div className="navbar-nav">
            <a className="nav-item nav-link" href="/">Home</a>
            <a className="nav-item nav-link active" href="/create_account">Create Account</a>
            <a className="nav-item nav-link" href="/deposit">Deposit</a>
            <a className="nav-item nav-link" href="/withdraw">Withdraw</a>
            <a className="nav-item nav-link" href="/all_data">All Data</a>
          </div>
          <span className="navbar-text ml-auto" id="userEmail">{userEmail}</span>
          <button className="btn btn-outline-light ml-2" id="logoutButton" style={{ display: userEmail ? 'block' : 'none' }} onClick={handleLogout}>Logout</button>
        </div>
      </nav>

      <main role="main" className="container mt-5">
        <div className="card">
          <div className="card-body">
            <h5 className="card-title text-center">Create Account</h5>
            <form id="createAccountForm" onSubmit={handleSubmit}>
              <div className="mb-3">
                <label htmlFor="nameInput" className="form-label">Name</label>
                <input type="text" className="form-control" id="nameInput" placeholder="Enter your name" value={formData.name} onChange={handleInputChange} />
              </div>
              <div className="mb-3">
                <label htmlFor="emailInput" className="form-label">Email address</label>
                <input type="email" className="form-control" id="emailInput" placeholder="Enter your email" value={formData.email} onChange={handleInputChange} />
              </div>
              <div className="mb-3">
                <label htmlFor="passwordInput" className="form-label">Password</label>
                <input type="password" className="form-control" id="passwordInput" placeholder="Create a password" value={formData.password} onChange={handleInputChange} />
              </div>
              <div className="d-grid gap-2">
                <button type="submit" className="btn btn-primary">Create Account</button>
                <button type="button" className="btn btn-secondary" id="addAnotherAccount" style={{ display: showAddAnother ? 'block' : 'none' }} onClick={handleAddAnother}>Add Another Account</button>
              </div>
            </form>
            <div className="d-grid gap-2 mt-3">
              <button className="btn btn-facebook" onClick={() => alert('Sign up with Facebook is not implemented in this demo.')}>Sign up with Facebook</button>
              <button className="btn btn-google" onClick={() => alert('Sign up with Google is not implemented in this demo.')}>Sign up with Google</button>
            </div>
          </div>
        </div>
      </main>

      {showSuccessModal && (
        <div className="modal" id="successModal" tabIndex="-1" role="dialog" aria-labelledby="successModalLabel" aria-hidden="true">
          <div className="modal-dialog" role="document">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title" id="successModalLabel">Account Created</h5>
                <button type="button" className="close" data-dismiss="modal" aria-label="Close" onClick={() => setShowSuccessModal(false)}>
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>
              <div className="modal-body">
                You've successfully created an account!
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-primary" data-dismiss="modal" onClick={() => setShowSuccessModal(false)}>Ok</button>
              </div>
            </div>
          </div>
        </div>
      )}

      <footer className="py-5" style={{ backgroundColor: '#efefef' }}>
        <div className="container">
          <p className="text-center">©2024 Very Bad Bank, Inc. All rights reserved.</p>
        </div>
      </footer>

      <style jsx>{`
        .navbar, .card-header {
          background-color: #004571;
          color: white;
        }
        .btn-primary, .nav-link:hover {
          background-color: #ff681f;
          border-color: #ff681f;
        }
        .card {
          background-color: #fdfcfd;
        }
        body, .card-text {
          color: #000000;
        }
        footer {
          background-color: #efefef;
        }
        .btn-facebook {
          background-color: #3b5998;
          color: white;
        }
        .btn-google {
          background-color: #db4437;
          color: white;
        }
      `}</style>
    </div>
  );
};

export default CreateAccount;
